import React from 'react';
import { Form, useLoaderData, useFetcher } from 'react-router-dom';
import { getContact, updateContact } from '../contacts';
import { TwitterOutlined, BookFilled, StarTwoTone } from '@ant-design/icons';
import { Row, Col, Button, Image, Space, Typography } from 'antd';
import { useTheme } from '@/hooks';
const { Text, Link, Title } = Typography;
export async function contactLoader({ params }: any) {
    const contact = await getContact(params.contactId);
    if (!contact) {
        throw new Response('', {
            status: 404,
            statusText: 'Not Found',
        });
    }
    return contact;
}
export async function favoriteAction({ request, params }: { request: any; params: any }) {
    let formData = await request.formData();
    return updateContact(params.contactId, {
        favorite: formData.get('favorite') === 'true',
    });
}
export function Contact() {
    const contact = useLoaderData() as any;
    return (
        <Row gutter={{ xs: 8, sm: 16, md: 24 }} className="w-full">
            <Col span={8}>
                <Image
                    key={contact.avatar}
                    src={contact.avatar || 'error'}
                    className="rounded"
                    fallback="data:image/png;base64,"
                />
            </Col>

            <Col span={4}>
                <Space direction="vertical">
                    <Title level={2}>
                        <Space>
                            {contact.first || contact.last ? (
                                <>
                                    {contact.first} {contact.last}
                                </>
                            ) : (
                                <>
                                    {'No'} {'Name'}
                                </>
                            )}
                            <Favorite contact={contact} />
                        </Space>
                    </Title>
                    <Title level={4}>
                        {contact.twitter && (
                            <Link href="`https://twitter.com/${contact.twitter}`" target="_blank">
                                <Space>
                                    <TwitterOutlined />
                                    {contact.twitter}
                                </Space>
                            </Link>
                        )}
                    </Title>
                    {contact.notes && (
                        <Text>
                            <Space>
                                <BookFilled />
                                {contact.notes}
                            </Space>
                        </Text>
                    )}

                    <Space>
                        <Form action="edit">
                            <Button htmlType="submit">Edit</Button>
                        </Form>
                        <Form
                            method="post"
                            action="destroy"
                            onSubmit={(event) => {
                                if (
                                    !window.confirm(
                                        'Please confirm you want to delete this record.'
                                    )
                                ) {
                                    event.preventDefault();
                                }
                            }}
                        >
                            <Button htmlType="submit" danger>
                                Delete
                            </Button>
                        </Form>
                    </Space>
                </Space>
            </Col>
        </Row>
    );
}
const Favorite = ({ contact }: any) => {
    let favorite = contact.favorite;
    const fetcher = useFetcher();
    if (fetcher.formData) {
        favorite = fetcher.formData.get('favorite') === 'true';
    }
    const { theme } = useTheme();
    return (
        <fetcher.Form method="post" className="inline-block">
            <Button
                name="favorite"
                value={favorite ? 'false' : 'true'}
                type="link"
                htmlType="submit"
                aria-label={favorite ? 'Remove from favorites' : 'Add to favorites'}
            >
                <Title level={2}>
                    <StarTwoTone twoToneColor={favorite ? theme.successColor : '#bfbfbf'} />
                </Title>
            </Button>
        </fetcher.Form>
    );
};
